import React, { Component} from 'react';
import "./films.css"
import Film from '../Film/Film';
import filmlist from '../../data/imdb_films'

class Films extends Component {
    render(){
        // console.log(filmlist[0])
        const filteredList = filmlist.filter( film => (film.Response === "True"));
        const genresFromFilms = filmlist.map(film => (film.Genre))
        // console.log(genresFromFilms);

        let genres = []
        for(let i=1; i< genresFromFilms.length ; i++){
            // console.log(genresFromFilms[i] && genresFromFilms[i].split(','))
            let arr = genresFromFilms[i] && genresFromFilms[i].split(',');
            // console.log("arr", arr)
            if(arr){
            for(let f=1; f< arr.length ; f++){
                // console.log(arr[f])
                genres.push(arr[f])

            }
        }
            // genres.push(genresFromFilms[i])
        }
// console.log("genres", genres)
function onlyUnique(value, index, self) { 
    return self.indexOf(value) === index;
}
        console.log(genres.filter( onlyUnique ))



        // console.log(genres)
        // console.log(filteredList)
        return(
            <ul>
                {filteredList
                .map( (film, index) => (

                    <Film 
                    key={index}
                    title={film.Title}
                    critic_consensus={film.critic_consensus}
                    plot={film.Plot}
                    imdbScore={film.Ratings && film.Ratings[0] && film.Ratings[0].Value}
                    meterScore={film.Ratings && film.Ratings[1] && film.Ratings[1].Value}
                    Metascore={film.Ratings && film.Ratings[2] && film.Ratings[2].Value}
                    imdbVotes={film.imdbVotes}
                    url={film.url}
                    Poster={film.Poster}
                    Awards={film.Awards}
                    Country={film.Country}
                    Language={film.Language}
                    />
                ))}
            </ul>
        )
    }
}

export default Films;

