import { Container, Row, Col } from 'react-bootstrap';


class App extends Component {
  render() {
    return (
      <div className="App">
        <Container>

  <Row>
    <Col xs={6}>      <Filter /></Col>
    <Col><Films /></Col>
  </Row>

</Container>;


      </div>
    );
  }
}

export default App;


