import React, { Component} from 'react';
import "./film.css"
class Film extends Component {
    render(){
        return(
            <li>
                <img src="https://m.media-amazon.com/images/M/MV5BMjA1NTEwMDMxMF5BMl5BanBnXkFtZTgwODkzMzI0MjE@._V1_SX300.jpg" alt=""/>
                <div>
                    <h3><a href=",https://www.rottentomatoes.com/m/haider">Haider</a> </h3>
                    <h4>Rotten Tomato Score: 90</h4>
                    <h4>IMDB Score: 9</h4>
                    <p className='film__plot'>Vishal Bhardwaj's adaptation of William Shakespeare's 'Hamlet', Haider - a young man returns home to Kashmir on receiving news of his father's disappearance. Not only does he learn that security forces have detained his father for harboring militants, but that his mother is in a relationship with his very own uncle. Intense drama follows between mother and son as both struggle to come to terms with news of his father's death. Soon Haider learns that his uncle is responsible for the gruesome murder, what follows is his journey to avenge his father's death."</p>
                </div>
            </li>
        )
    }
}

export default Film;
