import React, { Component} from 'react';
import "./film.css"

class Film extends Component {
    render(){
        const {title,meterScore, plot, critic_consensus,url, } = this.props;
        console.log(title)
        return(
            <li>
                <img src="https://m.media-amazon.com/images/M/MV5BMjA1NTEwMDMxMF5BMl5BanBnXkFtZTgwODkzMzI0MjE@._V1_SX300.jpg" alt=""/>
                <div className='film__info'>
                    <h3><a href={url}>{title}</a> </h3>
                    <h4>Rotten Tomato Score: {meterScore}</h4>
                    <h4>IMDB Score: 9</h4>
                    <p className='film__plot'>{plot}</p>
                </div>
            </li>
        )
    }
}

export default Film;
